import React, { useContext } from "react";
import { View, Modal, TouchableOpacity, Text } from "react-native";
import { ModuleContext } from "./Context";

const ColorPicker = () => {
  const { color, setColor, colorModalVisible, setColorModalVisible } =
    useContext(ModuleContext);
  return (
    <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
      <TouchableOpacity
        style={{
          width: 100,
          height: 100,
          backgroundColor: color,
          borderRadius: 50,
        }}
        onPress={() => setColorModalVisible(true)}
      />
      <Modal
        animationType="slide"
        transparent={false}
        visible={colorModalVisible}
      >
        <View
          style={{ flex: 1, alignItems: "center", justifyContent: "center", flexDirection: "row"}}
        >
          <TouchableOpacity
            style={{
              width: 50,
              height: 50,
              backgroundColor: "red",
              borderRadius: 50,
              marginBottom: 20,
            }}
            onPress={() => setColor("red")}
          />
          <TouchableOpacity
            style={{
              width: 50,
              height: 50,
              backgroundColor: "orange",
              borderRadius: 50,
              marginBottom: 20,
            }}
            onPress={() => setColor("orange")}
          />
          <TouchableOpacity
            style={{
              width: 50,
              height: 50,
              backgroundColor: "yellow",
              borderRadius: 50,
              marginBottom: 20,
            }}
            onPress={() => setColor("yellow")}
          />
           <TouchableOpacity
            style={{
              width: 50,
              height: 50,
              backgroundColor: "green",
              borderRadius: 50,
              marginBottom: 20,
            }}
            onPress={() => setColor("green")}
          />
           <TouchableOpacity
            style={{
              width: 50,
              height: 50,
              backgroundColor: "blue",
              borderRadius: 50,
              marginBottom: 20,
            }}
            onPress={() => setColor("blue")}
          />
           <TouchableOpacity
            style={{
              width: 50,
              height: 50,
              backgroundColor: "lightcoral",
              borderRadius: 50,
              marginBottom: 20,
            }}
            onPress={() => setColor("lightcoral")}
          />
          <TouchableOpacity
            style={{
              width: 50,
              height: 50,
              backgroundColor: "bisque",
              borderRadius: 50,
              marginBottom: 20,
            }}
            onPress={() => setColor("bisque")}
          />
          <TouchableOpacity
            style={{
              width: 50,
              height: 50,
              backgroundColor: "lavenderblush",
              borderRadius: 50,
              marginBottom: 20,
            }}
            onPress={() => setColor("lavenderblush")}
          />
          <TouchableOpacity
            style={{
              width: 50,
              height: 50,
              backgroundColor: "lightcyan",
              borderRadius: 50,
              marginBottom: 20,
            }}
            onPress={() => setColor("lightcyan")}
          />
          <TouchableOpacity
            style={{
              width: 50,
              height: 50,
              backgroundColor: "lavenderblush",
              borderRadius: 50,
              marginBottom: 20,
            }}
            onPress={() => setColor("lavenderblush")}
          />
          <TouchableOpacity onPress={() => setColorModalVisible(false)}>
            <Text>Done</Text>
          </TouchableOpacity>
        </View>
      </Modal>
    </View>
  );
};

export default ColorPicker;
